(142, 1, '2014-10-20 14:03:41', '2014-10-20 14:03:41', '<a href="http://project.cmi.hr.nl/2014_2015/iot_mt1f_t2/wordpress/wp-content/uploads/2014/09/Notulen-Vergaderingen-Team-900021.docx">Notulen-Vergaderingen-Team-90002</a>', 'Team 9000 Notulen vergaderingen', '', 'inherit', 'open', 'open', '', '94-autosave-v1', '', '', '2014-10-20 14:03:41', '2014-10-20 14:03:41', '', 94, 'http://project.cmi.hr.nl/2014_2015/iot_mt1f_t2/wordpress/?p=142', 0, 'revision', '', 0),
